import React from 'react';

const AboutMe = () => {
  const sectionStyles = {
    padding: '20px',
    display: 'flex', // Use flexbox for layout
    flexDirection: 'row', // Set row for larger screens
    alignItems: 'center', // Align items vertically in the center
    justifyContent: 'center', // Center content horizontally
    gap: '20px', // Add space between the image and the text
    flexWrap: 'wrap', // Allow wrapping for smaller screens
  };

  const textContainerStyles = {
    flex: '1', // Allow text to take equal space
    maxWidth: '500px', // Set a max width for the text container
  };

  const imageStyles = {
    flex: '1', // Allow the image to take equal space
    width: '100%', // Make image responsive
    maxWidth: '300px', // Set max width for the image
    height: 'auto',
    borderRadius: '10px', // Optional: Add rounded corners
  };

  return (
    <section id="about" style={sectionStyles}>
      {/* Image Section */}
      <img
        src={require('../images/myimage.jpeg')}
        alt="About Me"
        style={imageStyles}
      />

      {/* Text Section */}
      <div style={textContainerStyles}>
        <h2>About Me</h2>
        <p>
          I am passionate about creating clean and functional web designs. My
          goal is to help businesses build a strong online presence.
        </p>
      </div>
    </section>
  );
};

export default AboutMe;
